<?php

namespace Xiaomei\XiaomeiBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * UserRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class UserRepository extends EntityRepository
{
	public function findFullSingleContent($id){

		$query = $this->createQueryBuilder('u')
				->select('u,')	//hyper facile Ã  oublier
				 ->where('u.id = :id')  
				->leftJoin('u.', 'cat')	
			    ->leftJoin('c.user', 'user')		   
			    ->setParameter(':id', $id)
			    ->getQuery();

		$content = $query->getSingleResult();

		return $content;
//comment faire jointure many to many ; User et cours, pour avoir liste des cours dispensÃ© et liste de cours suivi

	}

}
